const initialState = [{
    id: 1,
    todo: 'Comer pan',
    done: false
}];

const todoReducer = (state = initialState, xxx) => {
    if (xxx?.type === 'agregar') {
        return [...state, xxx.payload];
    }
    return state;
}

let todos = todoReducer();

//////////nuevo objeto
const newTodo = {
    id: 2,
    todo: 'Tomar leche',
    done: false
};
////////////////////

const agregarTodoAction = {
    type: 'agregar',
    payload: newTodo
}

todos = todoReducer(todos, agregarTodoAction);

console.log(todos);
