        });
    }

    //AGREGAR un ToDo
    const handleSubmit = (e) => {
        e.preventDefault();

        if (description.trim().length <= 1) {
            return;
        }

        const newToDo = {
            id: new Date().getTime(),
            desc: description,
            done: false
        };

        const action = {
            type: 'add',
            payload: newToDo
        }

        dispatch(action);
        reset();
    }

    return (
        <div>
            <h1>ToDoApp ({todos.length})</h1>
            <hr></hr>
            <div className="row">
                <div className='col-7'>
                    {/* ToDoList, todos  handleDelete, handleToggle*/}
                    {
                        <ToDoList todos={todos} handleDelete={handleDelete} handleToggle={handleToggle} />
                    }
                </div>
                <div className='col-5'>
                    <ToDoAdd />
                </div>
            </div>
        </div>
    )
}

