import React, { useContext } from 'react'
import PropTypes from 'prop-types'
import { UserContext } from './UserContext';

const LoginScreen = () => {

  //1. obtener la refrencia al userContext
  //2. setUser




  const { user, setUser } = useContext(UserContext);
   const obj= {
    id: 123,
    name: 'Juan Carlos'
  }
  //console.log(setUser);
  return (
    <div>
      <h1>LoginScreen</h1>
      <hr></hr>
      <button className='btn btn-primary' onClick={() => setUser(...user, obj)}>
        Login
      </button>
    </div>
  )
}

