const initialState = [{
    id: 1,
    todo: 'Comer pan',
    done: false
}];

const todoReducer = (state = initialState, action) => {
    if (action.type==='agregar') {
        [...state, action.payload];
    }
    return state;
}

//////////nuevo objeto
const newtodo = {
    id: 2,
    todo: 'Tomar leche',
    done: false
};
////////////////////

const agregarTodoAction = {
    type: 'agregar',
    payload: newtodo
}

let todos = todoReducer();


console.log(todos);
