import React from 'react'
import PropTypes from 'prop-types'

const useCounter = (valorinicial) => {
    const [state, setState] = useState(valorinicial);

    let increment = () => {
        setState(valorinicial + 1);
    }
    let decrement = () => {
        setState(valorinicial - 1);
    }
    return {
        state,
        increment,
        decrement
    }
}

//useCounter.propTypes = {}

export default useCounter
