const initialState = [{
    id: 1,
    todo: 'Comer pan',
    done: false
}];

const todoReducer = (state = initialState, action) => {
    action();
    return state;
}

const newtodo = {
    id: 2,
    todo: 'Tomar leche',
    done: false
};


let todos = todoReducer();


console.log(todos);
